import './App.css';
import { useState } from "react";
import  Axios  from "axios";

// Swal is the same as tosta to make the alert look nice
import Swal from 'sweetalert2'

function App() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState();
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [year, setYear] = useState();
  const [id, setId] = useState();

  /// this is the button to edit 
  const[editar,setEditar] = useState(false)

  const[empleadosList,setEmpleados] = useState([])

  const add = () => {
    // Mostrar en consola los datos que se van a enviar
    console.log(nombre, edad, pais, cargo, year);

    Axios.post("http://localhost:5000/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: year
  })
  .then(response => {
    getEmpleados();
    limpiarCampos();
    Swal.fire({
      title: "User have been register succefully!",
      //! Remenber the way to concact 
      html:"<i>The user "+nombre+"</i>",
      text: "You clicked the button!",
      icon: "success",
      timer:5000 //* 3 second 
    });
  })
  .catch(error => {
      console.error("Error al registrar empleado:", error);
  });
  };


//! update
  const update = () => {
    // Mostrar en consola los datos que se van a enviar
    console.log(nombre, edad, pais, cargo, year);
   //* maje sure ut the same url as app.put("/update", (req, res) => { and use the id 
    Axios.put("http://localhost:5000/update", {
      id:id,
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: year
  })
  .then(response => {
    Swal.fire({
      title: "User Registered Successfully!",
      html: "<i>The user <strong>" + nombre + "</strong> has been registered successfully.</i>",
      text: "Thank you for registering the user.",
      icon: "success",
      timer: 3000 // 3 seconds
    });


    getEmpleados();
    limpiarCampos()// this functio is to clean after it been send 
      console.log("Empleado registrado:", response.data);
  })
  .catch(error => {
      console.error("Error al registrar empleado:", error);
  });
  };


  //! function lo clean after it 
  const limpiarCampos = () => {
    setNombre("");
    setEdad("");
    setPais("");
    setCargo("");
    setYear("");
    setId("");

    // to change the boolean at false, on that way will show the other button
    setEditar(false)

    //? that setEditar is the boolean to go back at it was 
//     <div className="">
//   {
//     editar ?
//       <button onClick={actualizarEmpleado}>Actualizar</button> :
//       <button onClick={add}>Registrar</button>
//   }
// </div>
  };

//! delete 

const deleleEmple = (id) => {

  Axios.delete(`http://localhost:5000/delete/${id}`)
    .then(response => {
      Swal.fire({
        title: "User Deleted Successfully!",
        html: `<i>User with ID <strong>${id}</strong> has been deleted.</i>`,
        buttons :[ "NO" ,"YES"],//
        icon: "success",
        timer: 3000 // 3 seconds

      });
    }).then(res=> {
        if(res) {

        }
    })
    .catch(error => {
      console.error("Error deleting employee:", error);
    });
};







// //* function the get the empeleados 
  const getEmpleados = () => {
    // Cambiar de POST a GET 
    //! the error was on the localhost that it was in 3001 
    Axios.get("http://localhost:5000/empleados")
      .then((response) => {
        setEmpleados(response.data); // in case in goes well 
      })

      //! I have to comment cause it gave me a lot of error, " what is the error"
      .catch((error) => {
        console.error("Error al obtener empleados:", error);
      });
  };

  getEmpleados();

  //* function to addate
  const editarEmpleado = (val) => {
    setEditar(true);

    setId(val.id);
    setNombre(val.nombre);
    setEdad(val.edad);
    setPais(val.pais);
    setCargo(val.cargo);
    setYear(val.anios);

  }


  // const mostrarDatos = () => {
  //   // here is the button, to add the people 
  //     console.log(nombre,edad,pais,cargo,year)
  //   }
  return (
    <div className="App">
      <h1>Formulario</h1>
      <div className="datos">
        <label>
          Nombre:
              {/*with onChange it target each time it happen any change 
                (event) it the atributo .value acces to the value 
              */}
          <input
          value={nombre}
            type="text"
            onChange={(event) => {
              setNombre(event.target.value);

            }}
          />
        </label>
        <br />
        <label>
          Edad:
          <input
          value={edad}
            type="number"
            onChange={(event) => {
              setEdad(Number(event.target.value));
            }}
          />
        </label>
        <br />
        <label>
          País:
          <input
          value={pais}
            type="text"
            onChange={(event) => {
              setPais(event.target.value);
            }}
          />
        </label>
        <br />
        <label>
          Cargo:
          <input
          value={cargo}
            type="text"
            onChange={(event) => {
              setCargo(event.target.value);
            }}
          />
        </label>
        <br />
        <label>
          Año:
          <input
            value={year}
            type="number"
            onChange={(event) => {
              setYear(Number(event.target.value));
            }}
          />
        </label>
        <br />
        <div className="">

          {/* this is a if else  */}
          {
            editar?
            <did>
              <button onClick={update}>Actualizar</button>
               <button onClick={limpiarCampos}>cancelar</button>
            </did> :
            <button onClick={add}>Registar</button>
            // <button onClick={add}  >actualizar</button>

          }

        </div>
      </div>
      <div className='lista'>
      {/* <button onClick={getEmpleados} >
          Listar
        </button> */}
      </div>

      <div className="listaDatos">
  <table className="tablaEmpleados">
    <thead>
      <tr>
        <th>id</th>
        <th>Nombre</th>
        <th>Edad</th>
        <th>País</th>
        <th>Cargo</th>
        <th>Años</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      {
        empleadosList.map((val, key) => (
          <tr key={key}>
            <td>{val.id}</td>
            <td>{val.nombre}</td>
            <td>{val.edad}</td>
            <td>{val.pais}</td>
            <td>{val.cargo}</td>
            <td>{val.anios}</td>
            <td>
              {/* this is the areo of the buttons */}
            <div className='buttosAct'>
            <button className='edict' onClick={()=> {
              editarEmpleado(val);
            }} >
          editar
        </button>
        {/* here is where is the delete , accessing the id with the value*/}
        <button className='delete' onClick={() => deleleEmple(val.id)} >Delete</button>
            </div>
            </td>
          </tr>
        ))
      }
    </tbody>
  </table>
</div>


    </div>
  );
}

export default App;
