import './App.css';
import { useState } from "react";
import  Axios  from "axios";


function App() {
  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [year, setYear] = useState(0);

  const[empleadosList,setEmpleados] = useState([])



  const add = () => {
    // Mostrar en consola los datos que se van a enviar
    console.log(nombre, edad, pais, cargo, year);

    Axios.post("http://localhost:3000/create", {
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: year
  })
  .then(response => {
    getEmpleados();

      console.log("Empleado registrado:", response.data);


  })
  .catch(error => {
      console.error("Error al registrar empleado:", error);
  });


  };

  const getEmpleados = () => {
    // Cambiar de POST a GET 
    //! the error was on the localhost that it was in 3001 
    Axios.get("http://localhost:3000/empleados")
      .then((response) => {
        setEmpleados(response.data); // in case in goes well 
      })
      .catch((error) => {
        console.error("Error al obtener empleados:", error);
      });
  };

  getEmpleados();


  // const mostrarDatos = () => {
  //   // here is the button, to add the people 
  //     console.log(nombre,edad,pais,cargo,year)
  //   }
  return (
    <div className="App">
      <h1>Formulario</h1>
      <div className="datos">
        <label>
          Nombre:
              {/*with onChange it target each time it happen any change 
                (event) it the atributo .value acces to the value 
              */}
          <input
            type="text"
            onChange={(event) => {
              setNombre(event.target.value);
            }}
          />
        </label>
        <br />
        <label>
          Edad:
          <input
            type="number"
            onChange={(event) => {
              setEdad(Number(event.target.value));
            }}
          />
        </label>
        <br />
        <label>
          País:
          <input
            type="text"
            onChange={(event) => {
              setPais(event.target.value);
            }}
          />
        </label>
        <br />
        <label>
          Cargo:
          <input
            type="text"
            onChange={(event) => {
              setCargo(event.target.value);
            }}
          />
        </label>
        <br />
        <label>
          Año:
          <input
            type="number"
            onChange={(event) => {
              setYear(Number(event.target.value));
            }}
          />
        </label>
        <br />
        <button onClick={add} >
          Add
        </button>
      </div>
      <div className='lista'>
      {/* <button onClick={getEmpleados} >
          Listar
        </button> */}
      </div>

        <div>

        </div>
      {
        empleadosList.map((val,key)=> {
          // return<div className=''> {val.nombre}</div>
          return <div className='' key={key}>{val.nombre}</div>;
        })
      }

    </div>
  );
}

export default App;
